import React, { useState } from "react";

import News from "./News";
import {
  BrowserRouter as Router,
  Switch,
  Route,
  useLocation,
} from "react-router-dom";

import { AnimatePresence, motion } from "framer-motion/dist/framer-motion";

const AnimatedRoutes = () => {
  const pageSize = 5;
  const apiKey = "0d0df8efeaf542be8b538ad5dbd71953";
  // process.env.REACT_APP_NEWS_API
  const [progress, setProgress] = useState(0);

  const toggleFunction = () => {
    if (mode === "light") {
      setMode("dark");
      document.body.style.backgroundColor = "#150020"; //#360036
      showAlert("success", "Dark mode activated");
    } else {
      setMode("light");
      document.body.style.backgroundColor = "white";
      showAlert("success", "Light mode activated");
    }
  };

  const [alert, setAlert] = useState(null);

  const showAlert = (type, message) => {
    setAlert({
      type: type,
      msg: message,
    });
    setTimeout(() => {
      setAlert(null);
    }, 2000);
  };

  const [mode, setMode] = useState("light");
  const [backg, setBackg] = useState("backgroundColor: white");
  const location = useLocation();

  return (
    <AnimatePresence>
      <Switch location={location} key={location.pathname}>
        <Route exact path="/">
          <motion.div
            initial={{ width: "50%" }}
            animate={{ width: "100%" }}
            exit={{ x: window.innerWidth, transition: { duration: 0.4 } }}
          >
            <News
              mode={mode}
              setProgress={setProgress}
              apiKey={apiKey}
              key="general"
              pageSize={pageSize}
              country="in"
              category="general"
            />
          </motion.div>
        </Route>
        <Route exact path="/business">
          <motion.div
            initial={{ width: 0 }}
            animate={{ width: "100%" }}
            exit={{ x: window.innerWidth, transition: { duration: 0.4 } }}
          >
            <News
              mode={mode}
              setProgress={setProgress}
              apiKey={apiKey}
              key="business"
              pageSize={pageSize}
              country="in"
              category="business"
            />
          </motion.div>
        </Route>
        <Route exact path="/entertainment">
          <motion.div
            initial={{ width: 0 }}
            animate={{ width: "100%" }}
            exit={{ x: window.innerWidth, transition: { duration: 0.4 } }}
          >
            <News
              mode={mode}
              setProgress={setProgress}
              apiKey={apiKey}
              key="entertainment"
              pageSize={pageSize}
              country="in"
              category="entertainment"
            />
          </motion.div>
        </Route>
        <Route exact path="/general">
          <motion.div
            initial={{ width: 0 }}
            animate={{ width: "100%" }}
            exit={{ x: window.innerWidth, transition: { duration: 0.4 } }}
          >
            <News
              mode={mode}
              setProgress={setProgress}
              apiKey={apiKey}
              key="general"
              pageSize={pageSize}
              country="in"
              category="general"
            />
          </motion.div>
        </Route>
        <Route exact path="/health">
          <motion.div
            initial={{ width: 0 }}
            animate={{ width: "100%" }}
            exit={{ x: window.innerWidth, transition: { duration: 0.4 } }}
          >
            <News
              mode={mode}
              setProgress={setProgress}
              apiKey={apiKey}
              key="health"
              pageSize={pageSize}
              country="in"
              category="health"
            />
          </motion.div>
        </Route>
        <Route exact path="/science">
          <motion.div
            initial={{ width: 0 }}
            animate={{ width: "100%" }}
            exit={{ x: window.innerWidth, transition: { duration: 0.4 } }}
          >
            <News
              mode={mode}
              setProgress={setProgress}
              apiKey={apiKey}
              key="science"
              pageSize={pageSize}
              country="in"
              category="science"
            />
          </motion.div>
        </Route>
        <Route exact path="/sports">
          <motion.div
            initial={{ width: 0 }}
            animate={{ width: "100%" }}
            exit={{ x: window.innerWidth, transition: { duration: 0.4 } }}
          >
            <News
              mode={mode}
              setProgress={setProgress}
              apiKey={apiKey}
              key="sports"
              pageSize={pageSize}
              country="in"
              category="sports"
            />
          </motion.div>
        </Route>
        <Route exact path="/technology">
          <motion.div
            initial={{ width: 0 }}
            animate={{ width: "100%" }}
            exit={{ x: window.innerWidth, transition: { duration: 0.4 } }}
          >
            <News
              mode={mode}
              setProgress={setProgress}
              apiKey={apiKey}
              key="technology"
              pageSize={pageSize}
              country="in"
              category="technology"
            />
          </motion.div>
        </Route>
      </Switch>
    </AnimatePresence>
  );
};
export default AnimatedRoutes;
