import "./App.css";

import React, { useState } from "react";
import NavBar from "./components/NavBar";
// import News from "./components/News";
import {
  BrowserRouter as Router,
  Switch,
  Route,
  useLocation,
} from "react-router-dom";
import LoadingBar from "react-top-loading-bar";
import { AnimatePresence } from "framer-motion/dist/framer-motion";
import AnimatedRoutes from "./components/AnimatedRoutes";

const App = () => {
  const pageSize = 5;
  const apiKey = "af96914f4d0d49e6b3149fc1c6fa764a";
  // process.env.REACT_APP_NEWS_API
  const [progress, setProgress] = useState(0);

  const toggleFunction = () => {
    if (mode === "light") {
      setMode("dark");
      document.body.style.backgroundImage = `url("/darkMode_bg.png")`; //"#150020"; //#360036

      showAlert("success", "Dark mode activated");
    } else {
      setMode("light");
      document.body.style.backgroundImage = `url("https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRQ2tzV8X5_txDPbiXKNfoXQO09D_0MuvWTlX-1kHz-&s")`;
      showAlert("success", "Light mode activated");
    }
  };

  const [alert, setAlert] = useState(null);

  const showAlert = (type, message) => {
    setAlert({
      type: type,
      msg: message,
    });
    setTimeout(() => {
      setAlert(null);
    }, 2000);
  };

  const [mode, setMode] = useState("light");

  return (
    <div>
      <Router>
        <NavBar toggle={toggleFunction} mode={mode} />
        <LoadingBar height={3} color="#f11946" progress={progress} />
        <AnimatedRoutes mode={mode} setProgress={setProgress} />
      </Router>
    </div>
  );
};

export default App;
