import "./App.css";

import React, { useState } from "react";
import NavBar from "./components/NavBar";
import News from "./components/News";
import {
  BrowserRouter as Router,
  Switch,
  Route,
  useLocation,
} from "react-router-dom";
import LoadingBar from "react-top-loading-bar";
import { AnimatePresence } from "framer-motion/dist/framer-motion";

function AnimatedRoutes() {
  const location = useLocation();

  return (
    <AnimatePresence>
      <Switch location={location} key={location.pathname}>
        <Route exact path="/">
          <News
            mode={mode}
            setProgress={setProgress}
            apiKey={apiKey}
            key="general"
            pageSize={pageSize}
            country="in"
            category="general"
          />
        </Route>
        <Route exact path="/business">
          <News
            mode={mode}
            setProgress={setProgress}
            apiKey={apiKey}
            key="business"
            pageSize={pageSize}
            country="in"
            category="business"
          />
        </Route>
        <Route exact path="/entertainment">
          <News
            mode={mode}
            setProgress={setProgress}
            apiKey={apiKey}
            key="entertainment"
            pageSize={pageSize}
            country="in"
            category="entertainment"
          />
        </Route>
        <Route exact path="/general">
          <News
            mode={mode}
            setProgress={setProgress}
            apiKey={apiKey}
            key="general"
            pageSize={pageSize}
            country="in"
            category="general"
          />
        </Route>
        <Route exact path="/health">
          <News
            mode={mode}
            setProgress={setProgress}
            apiKey={apiKey}
            key="health"
            pageSize={pageSize}
            country="in"
            category="health"
          />
        </Route>
        <Route exact path="/science">
          <News
            mode={mode}
            setProgress={setProgress}
            apiKey={apiKey}
            key="science"
            pageSize={pageSize}
            country="in"
            category="science"
          />
        </Route>
        <Route exact path="/sports">
          <News
            mode={mode}
            setProgress={setProgress}
            apiKey={apiKey}
            key="sports"
            pageSize={pageSize}
            country="in"
            category="sports"
          />
        </Route>
        <Route exact path="/technology">
          <News
            mode={mode}
            setProgress={setProgress}
            apiKey={apiKey}
            key="technology"
            pageSize={pageSize}
            country="in"
            category="technology"
          />
        </Route>
      </Switch>
    </AnimatePresence>
  );
}
export default AnimatedRoutes;
