import React from "react";
import { useFormik } from "formik";
import { signInSchema } from "../schemas";
import { signUpSchema } from "../schemas/signUp";
import { Link } from "react-router-dom";
import "./style.css";
var init = true;
var schema = signInSchema;

const Login = () => {
  const signUpTransition = () => {
    global = init;

    const container = document.querySelector(".loginContainer");
    container.classList.add("sign_up_mode");
    init = true;
    schema = signUpSchema;
  };

  const signInTransition = () => {
    global = init;

    const container = document.querySelector(".loginContainer");
    container.classList.remove("sign_up_mode");
    init = false;
    schema = signInSchema;
  };
  var initialValues = {};

  if (!init) {
    // schema = signInSchema;
    initialValues = {
      username: "",
      password: "",
      // email: "",
      // username2: "",
      // password2: "",
    };
  } else {
    // schema = signUpSchema;
    initialValues = {
      email: "",
      username2: "",
      password2: "",
    };
  }
  // const initialValues2 = {
  //   email: "",
  //   username2: "",
  //   password2: "",
  // };

  const { values, errors, handleBlur, touched, handleChange, handleSubmit } =
    useFormik({
      initialValues: initialValues,

      validationSchema: schema,
      // validationSchema: signUpSchema,
      onSubmit: (values, action) => {
        console.log(values);
        action.resetForm();
      },
    });

  console.log(errors);

  return (
    <>
      <div className="body">
        <div className="loginContainer">
          <div className="closeArea">
            <Link className="linkText" to="/">
              <button className="closeButtonLeft">
                <i class="fa-solid fa-xmark"></i>
              </button>
            </Link>
          </div>
          <div className="signin-signup">
            <form className="sign-in-form" onSubmit={handleSubmit}>
              <h2 className="title">Log In</h2>
              <div className="input-field">
                <i className="fas fa-user" htmlFor="username"></i>

                <input
                  type="text"
                  placeholder="Username"
                  name="username"
                  id="username"
                  value={values.username}
                  onChange={handleChange}
                  onBlur={handleBlur}
                />
                {errors.username && touched.username ? (
                  <small className="form-error">{errors.username}</small>
                ) : null}
              </div>

              <div className="input-field">
                <i className="fas fa-lock" htmlFor="password"></i>

                <input
                  type="password"
                  placeholder="Password"
                  name="password"
                  id="password"
                  autoComplete="off"
                  value={values.password}
                  onChange={handleChange}
                  onBlur={handleBlur}
                />
                {errors.password && touched.password ? (
                  <small className="form-error">{errors.password}</small>
                ) : null}
              </div>
              <input type="submit" value="Login" className="bttn" />

              <p className="social-text">Or sign in with social platform</p>
              <div className="social-media">
                <a href="/" className="social-icon">
                  <i className="fab fa-facebook"></i>
                </a>
                <a href="/" className="social-icon">
                  <i className="fab fa-twitter"></i>
                </a>
                <a href="/" className="social-icon">
                  <i className="fab fa-google"></i>
                </a>
                <a href="/" className="social-icon">
                  <i className="fab fa-linkedin-in"></i>
                </a>
              </div>
            </form>

            <form action="" className="sign-up-form" onSubmit={handleSubmit}>
              <h2 className="title">Sign Up</h2>
              <div className="input-field">
                <i className="fas fa-user"></i>

                <input
                  type="text"
                  placeholder="Username"
                  name="username2"
                  id="username2"
                  autoComplete="off"
                  value={values.username2}
                  onChange={handleChange}
                  onBlur={handleBlur}
                />
                {errors.username2 && touched.username2 ? (
                  <small className="form-error">{errors.username2}</small>
                ) : null}
              </div>

              <div className="input-field">
                <i className="fas fa-envelope"></i>
                <input
                  type="text"
                  placeholder="Email"
                  name="email"
                  id="email"
                  autoComplete="off"
                  value={values.email}
                  onChange={handleChange}
                  onBlur={handleBlur}
                />
                {errors.email && touched.email ? (
                  <small className="form-error">{errors.email}</small>
                ) : null}
              </div>

              <div className="input-field">
                <i className="fas fa-lock"></i>
                <input
                  type="password"
                  placeholder="Password"
                  name="password2"
                  id="password2"
                  autoComplete="off"
                  value={values.password2}
                  onChange={handleChange}
                  onBlur={handleBlur}
                />
                {errors.password2 && touched.password2 ? (
                  <small className="form-error">{errors.password2}</small>
                ) : null}
              </div>
              <input type="submit" value="Sign up" className="bttn" />
              <p className="social-text">Or sign in with social platform</p>
              <div className="social-media">
                <a href="/" className="social-icon">
                  <i className="fab fa-facebook"></i>
                </a>
                <a href="/" className="social-icon">
                  <i className="fab fa-twitter"></i>
                </a>
                <a href="/" className="social-icon">
                  <i className="fab fa-google"></i>
                </a>
                <a href="/" className="social-icon">
                  <i className="fab fa-linkedin-in"></i>
                </a>
              </div>
            </form>
          </div>
          <div className="panel-container">
            <div className="panel left-panel">
              <div className="content">
                <h3>Already signed up?</h3>
                <p>
                  Lorem ipsum, dolor sit amet consectetur adipisicing elit. Illo
                  ut quas deleniti recusandae quam quidem!
                </p>
                <button
                  className="bttn "
                  id="signInBtn"
                  onClick={signInTransition}
                >
                  Log in
                </button>
              </div>
              {/* <i class="fa-solid fa-user fa-flip image"></i> */}
              <img
                src="https://img.lovepik.com/element/45009/2341.png_860.png"
                alt=""
                className="image"
              />
            </div>

            <div className="panel right-panel">
              <div className="content">
