import React from "react";
import { useFormik } from "formik";
import { signInSchema } from "../schemas";
import { signUpSchema } from "../schemas";

const initialValues = {
  username: "",
  password: "",
  email: "",
};

const Login = () => {
  const signUpTransition = () => {
    const container = document.querySelector(".loginContainer");
    container.classList.add("sign_up_mode");
  };

  const signInTransition = () => {
    const container = document.querySelector(".loginContainer");
    container.classList.remove("sign_up_mode");
  };

  const { values, errors, handleBlur, touched, handleChange, handleSubmit } =
    useFormik({
      initialValues: initialValues,
      validationSchema: signInSchema,
      validationSchema: signUpSchema,
      onSubmit: (values, action) => {
        console.log(values);
        action.resetForm();
      },
    });

  console.log(errors);

  return (
    <div className="body">
      <div className="loginContainer">
        <div className="signin-signup">
          <form className="sign-in-form" onSubmit={handleSubmit}>
            <h2 className="title">Sign In</h2>
            <div className="input-field">
              <i className="fas fa-user" htmlFor="username"></i>
              <input
                type="text"
                placeholder="Username"
                name="username"
                id="username"
                value={values.username}
                onChange={handleChange}
                onBlur={handleBlur}
              />
              {errors.username && touched.username ? (
                <small className="form-error">{errors.username}</small>
              ) : null}
            </div>

            <div className="input-field">
              <i className="fas fa-lock" htmlFor="password"></i>
              <input
                type="password"
                placeholder="Password"
                name="password"
                id="password"
                autoComplete="off"
                value={values.password}
                onChange={handleChange}
                onBlur={handleBlur}
              />
              {errors.password && touched.password ? (
                <small className="form-error">{errors.password}</small>
              ) : null}
            </div>
            <input type="submit" value="Login" className="bttn" />
            <p className="social-text">Or sign in with social platform</p>
            <div className="social-media">
              <a href="/" className="social-icon">
                <i className="fab fa-facebook"></i>
              </a>
              <a href="/" className="social-icon">
                <i className="fab fa-twitter"></i>
              </a>
              <a href="/" className="social-icon">
                <i className="fab fa-google"></i>
              </a>
              <a href="/" className="social-icon">
                <i className="fab fa-linkedin-in"></i>
              </a>
            </div>
          </form>

          <form action="" className="sign-up-form" onSubmit={handleSubmit}>
            <h2 className="title">Sign Up</h2>
            <div className="input-field">
              <i className="fas fa-user"></i>
              <input
                type="text"
                placeholder="Username"
                name="username"
                id="username"
                autoComplete="off"
                value={values.username}
                onChange={handleChange}
                onBlur={handleBlur}
              />
              {errors.username && touched.username ? (
                <small className="form-error">{errors.username}</small>
              ) : null}
            </div>

            <div className="input-field">
              <i className="fas fa-envelope"></i>
              <input
                type="text"
                placeholder="Email"
                name="email"
                id="email"
                autoComplete="off"
                value={values.email}
                onChange={handleChange}
                onBlur={handleBlur}
              />
              {errors.email && touched.email ? (
                <small className="form-error">{errors.email}</small>
              ) : null}
            </div>

            <div className="input-field">
              <i className="fas fa-lock"></i>
              <input
                type="password"
                placeholder="Password"
                name="password"
                id="password"
                autoComplete="off"
                value={values.password}
                onChange={handleChange}
                onBlur={handleBlur}
              />
              {errors.password && touched.password ? (
                <small className="form-error">{errors.password}</small>
              ) : null}
            </div>
            <input type="submit" value="Sign up" className="bttn" />
            <p className="social-text">Or sign in with social platform</p>
            <div className="social-media">
              <a href="/" className="social-icon">
                <i className="fab fa-facebook"></i>
              </a>
              <a href="/" className="social-icon">
                <i className="fab fa-twitter"></i>
              </a>
              <a href="/" className="social-icon">
                <i className="fab fa-google"></i>
              </a>
              <a href="/" className="social-icon">
                <i className="fab fa-linkedin-in"></i>
              </a>
            </div>
          </form>
        </div>
        <div className="panel-container">
          <div className="panel left-panel">
            <div className="content">
              <h3>Member of Brand?</h3>
              <p>
                Lorem ipsum, dolor sit amet consectetur adipisicing elit. Illo
                ut quas deleniti recusandae quam quidem!
              </p>
              <button
                className="bttn "
                id="signInBtn"
                onClick={signInTransition}
              >
                Sign in
              </button>
            </div>
            {/* <img src="signin.svg" alt="" className="image" /> */}
          </div>

          <div className="panel right-panel">
            <div className="content">
              <h3>New to Brand?</h3>
              <p>
                Lorem ipsum, dolor sit amet consectetur adipisicing elit. Illo
                ut quas deleniti recusandae quam quidem!
              </p>
              <button
                className="bttn "
                id="signUpBtn"
                onClick={signUpTransition}
              >
                Sign up
              </button>
            </div>
            {/* <img src="signup.svg" alt="" className="image" /> */}
          </div>
        </div>
      </div>
    </div>
  );
};

export default Login;
