import React from "react";
import { useFormik } from "formik";
import { signInSchema } from "../schemas";

const Feedback = () => {
  const initialValues = {
    username: "",
    password: "",
    // email: "",
    // username2: "",
    // password2: "",
  };

  const { values, errors, handleBlur, touched, handleChange, handleSubmit } =
    useFormik({
      initialValues: initialValues,

      validationSchema: signInSchema,
      // validationSchema: signUpSchema,
      onSubmit: (values, action) => {
        console.log(values);
        action.resetForm();
      },
    });

  console.log(errors);

  return (
    <div className="body">
      <div className="loginContainer">
        <div className="signin-signup">
          <form className="sign-in-form" onSubmit={handleSubmit}>
            {/* <h2 className="title"></h2> */}
            <div>
              {/* <i className="fas fa-user" htmlFor="username"></i> */}
              <div className="feedHeadline">Give your feedback here</div>
              <textarea
                className="textarea"
                rows="4"
                column="50"
                placeholder="Enter text"
                name="username"
                id="username"
                value={values.username}
                onChange={handleChange}
                onBlur={handleBlur}
              />
              {/* {errors.username && touched.username ? (
                <small className="form-error">{errors.username}</small>
              ) : null}
            </div>

            <div className="input-field">
              <i className="fas fa-lock" htmlFor="password"></i>

              <input
                type="password"
                placeholder="Password"
                name="password"
                id="password"
                autoComplete="off"
                value={values.password}
                onChange={handleChange}
                onBlur={handleBlur}
              />
              {errors.password && touched.password ? (
                <small className="form-error">{errors.password}</small>
              ) : null} */}
            </div>
            <input type="submit" value="Submit" className="bttn" />
          </form>
          <form className="sign-up-form" onSubmit={handleSubmit}></form>
        </div>
        <div className="panel-container">
          <div className="panel left-panel">
            <div className="content">
              <h3>Member of Brand?</h3>
              <p>
                Lorem ipsum, dolor sit amet consectetur adipisicing elit. Illo
                ut quas deleniti recusandae quam quidem!
              </p>
            </div>
            {/* <img src="signin.svg" alt="" className="image" /> */}
          </div>

          <div className="panel right-panel">
            <div id="feedback" className="content">
              <h3 className="feedText" style={{ fontSize: 50 }}>
                FeedBack Form
              </h3>
            </div>
            {/* <i class="fa-solid fa-comment fa-bounce fa-2xl feedIcon"></i> */}
            <i
              class="fa-duotone fa-newspaper fa-fade fa-2xl"
              style="--fa-primary-color: #000000; --fa-primary-opacity: 0.8; --fa-secondary-color: #d300d6; --fa-secondary-opacity: 1;"
            ></i>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Feedback;
