import "./App.css";

import React, { useState } from "react";
import NavBar from "./components/NavBar";
import News from "./components/News";
import {
  BrowserRouter as Router,
  Switch,
  Route,
  useLocation,
} from "react-router-dom";
import LoadingBar from "react-top-loading-bar";
import { AnimatePresence } from "framer-motion/dist/framer-motion";

const App = () => {
  const pageSize = 5;
  const apiKey = "0d0df8efeaf542be8b538ad5dbd71953";
  // process.env.REACT_APP_NEWS_API
  const [progress, setProgress] = useState(0);

  const toggleFunction = () => {
    if (mode === "light") {
      setMode("dark");
      document.body.style.backgroundColor = "#150020"; //#360036
      showAlert("success", "Dark mode activated");
    } else {
      setMode("light");
      document.body.style.backgroundColor = "white";
      showAlert("success", "Light mode activated");
    }
  };

  const [alert, setAlert] = useState(null);

  const showAlert = (type, message) => {
    setAlert({
      type: type,
      msg: message,
    });
    setTimeout(() => {
      setAlert(null);
    }, 2000);
  };

  const [mode, setMode] = useState("light");
  // const [backg, setBackg] = useState("backgroundColor: white")

  return (
    <div>
      <Router>
        <NavBar toggle={toggleFunction} mode={mode} />
        <LoadingBar height={3} color="#f11946" progress={progress} />
        <AnimatedRoutes />
      </Router>
    </div>
  );
};

export default App;
